{"windows":"// Place your key bindings in this file to override the defaults
[
    {
        "key": "ctrl+enter",
        "command": "-jupyter.runcurrentcell",
        "when": "editorTextFocus && isWorkspaceTrusted && jupyter.hascodecells && !editorHasSelection && !jupyter.havenativecells && !notebookEditorFocused"
    },
    {
        "key": "ctrl+enter",
        "command": "-jupyter.runByLineStop",
        "when": "notebookCellResource in 'jupyter.notebookeditor.runByLineCells'"
    },
    {
        "key": "ctrl+shift+enter",
        "command": "editor.action.insertLineAfter",
        "when": "editorTextFocus && !editorReadonly"
    },
    {
        "key": "ctrl+enter",
        "command": "-editor.action.insertLineAfter",
        "when": "editorTextFocus && !editorReadonly"
    },
    {
        "key": "ctrl+k ctrl+c",
        "command": "-editor.action.addCommentLine",
        "when": "editorTextFocus && !editorReadonly"
    },
    {
        "key": "ctrl+shift+1",
        "command": "editor.action.removeCommentLine",
        "when": "editorTextFocus && !editorReadonly"
    },
    {
        "key": "ctrl+k ctrl+u",
        "command": "-editor.action.removeCommentLine",
        "when": "editorTextFocus && !editorReadonly"
    },
    {
        "key": "ctrl+t",
        "command": "python.createTerminal"
    },
    {
        "key": "ctrl+shift+o",
        "command": "workbench.action.files.openFolder",
        "when": "openFolderWorkspaceSupport"
    },
    {
        "key": "ctrl+k ctrl+o",
        "command": "-workbench.action.files.openFolder",
        "when": "openFolderWorkspaceSupport"
    },
    {
        "key": "ctrl+1",
        "command": "editor.action.commentLine",
        "when": "editorTextFocus && !editorReadonly"
    },
    {
        "key": "ctrl+oem_2",
        "command": "-editor.action.commentLine",
        "when": "editorTextFocus && !editorReadonly"
    },
    {
        "key": "ctrl+enter",
        "command": "typst-preview.preview",
        "when": "editorLangId == 'typst'"
    },
    {
        "key": "ctrl+k v",
        "command": "-typst-preview.preview",
        "when": "editorLangId == 'typst'"
    },
    {
        "key": "ctrl+f4",
        "command": "workbench.action.terminal.killAll"
    },
    {
        "key": "alt+a",
        "command": "notebook.cell.executeCellsAbove"
    },
    {
        "key": "ctrl+enter",
        "command": "python.execInTerminal-icon"
    },
    {
        "key": "ctrl+shift+k",
        "command": "-rmarkdown.rmarkdown-core.Knit",
        "when": "editorTextFocus"
    },
    {
        "key": "ctrl+enter",
        "command": "-r.runSelection",
        "when": "editorTextFocus && editorLangId == 'r'"
    },
    {
        "key": "ctrl+shift+s",
        "command": "-workbench.action.files.saveLocalFile",
        "when": "remoteFileDialogVisible"
    },
    {
        "key": "ctrl+shift+s",
        "command": "-workbench.action.files.saveAs"
    },
    {
        "key": "ctrl+shift+k",
        "command": "-r.knitRmd",
        "when": "editorTextFocus && editorLangId == 'rmd'"
    },
    {
        "key": "ctrl+enter",
        "command": "-r.runSelection",
        "when": "editorTextFocus && editorLangId == 'rmd'"
    },
    {
        "key": "ctrl+i",
        "command": "-markdown.extension.editing.toggleItalic",
        "when": "editorTextFocus && !editorReadonly && editorLangId =~ /^markdown$|^rmd$|^quarto$/"
    },
    {
        "key": "ctrl+alt+up",
        "command": "workbench.action.toggleMaximizedPanel"
    },
    {
        "key": "ctrl+f",
        "command": "-workbench.action.terminal.focusFind",
        "when": "terminalFocus"
    },
    {
        "key": "ctrl+f",
        "command": "workbench.action.terminal.sendSequence",
        "args": { "text": "\u0006" }, // Env√≠a Ctrl+F al terminal
        "when": "terminalFocus"
    },
    {
        "key": "ctrl+alt+s",
        "command": "latex-workshop.build",
        "when": "!virtualWorkspace"
    },
    {
        "key": "shift+enter",
        "command": "workbench.action.terminal.runSelectedText"
    },
]","linux":"// Place your key bindings in this file to override the defaults
